async function logAndExit(logger2, message, error) {
  try {
    logger2.error(message, error);
  } catch (logErr) {
    const fs = await import("fs");
    const errorLog = `${(/* @__PURE__ */ new Date()).toISOString()} ${message}: ${error?.stack || error}
`;
    try {
      fs.appendFileSync("logs/mcp-server-error.log", errorLog);
    } catch (fsErr) {
    }
  }
  setTimeout(() => process.exit(1), 100);
  return new Promise(() => {
  });
}
